with open('input.txt', 'r') as file: print(sum([(6 if ['X', 'Y', 'Z'].index(m) > ['A', 'B', 'C'].index(n) and ['X', 'Y', 'Z'].index(m) % 2 != ['A', 'B', 'C'].index(n) or ['A', 'B', 'C'].index(n) == 2 and ['X', 'Y', 'Z'].index(m) == 0 else 3 * int(['X', 'Y', 'Z'].index(m) == ['A', 'B', 'C'].index(n))) + (['X', 'Y', 'Z'].index(m) + 1) for n, m in [game.split(' ') for game in file.read().strip().split('\n')]]))
